mapper.select:

   /* board.boardList */

	select  B.*
	      , U.user_nm  as reg_user_nm
	from tb_board B left outer join tb_user U on ( B.reg_user_no = U.user_no)



	order by ifnull(B.top_notice_yn, 'N') desc, B.top_reg_no desc, all_reg_no asc

     limit  #{start_row}, #{end_row}


/* board.boardCount */

    SELECT  count(*) 
	from tb_board B left outer join tb_user U on ( B.reg_user_no = U.user_no)


         /* board.boardDetail */

			select  B.*
			      , U.user_nm  as reg_user_nm
			      , (select nm from tb_code where REPR_CD = 'NOT' and CD = case when B.NOTICE_TYPE is null or B.NOTICE_TYPE = '' then 'NOT002' else B.NOTICE_TYPE end ) as notice_type_nm
			from tb_board B left outer join tb_user U on ( B.reg_user_no = U.user_no)
		    where  brd_reg_no = #{brd_reg_no}

mapper.update:

/* board.updateBoardUseYn */

UPDATE  tb_board
SET		use_yn = 'N'
WHERE   brd_reg_no = #{brd_reg_no}

mapper.insert:

 	/* board.insertBoard */

	INSERT INTO tb_board
		(
			  brd_kind
			, ttl
			, ctnt
			, view_cnt
			, use_yn
			, reg_dt
			, top_notice_yn
	 		, notice_type

	        , brd_fr_dt
	        , brd_to_dt

			 , REG_USER_NO    


		) VALUES (

			  upper(#{brd_kind})
			, #{ttl}
			, #{ctnt}
			, 0
			, #{use_yn}
			, DATE_FORMAT(now()	, '%Y%m%d%H%i%s')
			, #{top_notice_yn}
	 		, #{notice_type}

	        , #{brd_fr_dt}
	        , #{brd_to_dt}

	  , #{token_user_no}     

		)


	        SELECT LAST_INSERT_ID()

mapper.update:


	 	/* board.insertAfterBoardUpdate */                                                                

		UPDATE tb_board
		SET       depth       = #{depth}
				, all_reg_no  = #{all_reg_no}
				, top_reg_no  = #{top_reg_no}

          WHERE brd_reg_no = #{brd_reg_no}



	 	/* board.updateBoard */                                                                

		UPDATE tb_board
		SET       ttl           = #{ttl}
				, ctnt          = #{ctnt}
				, use_yn        = #{use_yn}
				, top_notice_yn = #{top_notice_yn}
		 		, notice_type 	= #{notice_type}

		        , brd_fr_dt     = #{brd_fr_dt}
		        , brd_to_dt     = #{brd_to_dt}

 				, upd_dt        = DATE_FORMAT(now()	, '%Y%m%d%H%i%s')    
				, upd_reg_no = #{token_user_no}     
          WHERE brd_reg_no = #{brd_reg_no}
