mapper.select:
/* code.codeCount */

SELECT  count(*)
FROM  TB_CODE A

/* code.codeList */
SELECT * FROM TB_CODE A
ORDER BY sort_seq asc, cd

mapper.insert:
 	/* code.insertCode */

	INSERT INTO TB_CODE
		(
		   repr_cd
         , cd
         , nm
         , dsc
         , use_yn
         , val1
         , val2
         , val3
         , sort_seq
		) VALUES (
		   #{repr_cd}
         , #{cd}
         , #{nm}
         , #{dsc}
         , #{use_yn}
         , #{val1}
         , #{val2}
         , #{val3}
         , #{sort_seq}
		)

mapper.update:
 	/* code.updateCode */

	UPDATE TB_CODE
	SET	   nm        = #{nm}
         , dsc       = #{dsc}
         , use_yn    = #{use_yn}
         , val1	     = #{val1}
         , val2	     = #{val2}
         , val3	     = #{val3}
         , sort_seq	 = #{sort_seq}
	WHERE  repr_cd   = #{repr_cd}
    AND    cd        = #{cd}

mapper.select:
 	/* code.codeDetail */

	SELECT *
	FROM TB_CODE
	WHERE  repr_cd = #{repr_cd}
    AND    cd      = #{cd}

/* code.codeReprList */
SELECT * FROM TB_REPRCODE A
ORDER BY repr_cd

mapper.insert:
 	/* code.insertRepr */

	INSERT INTO TB_REPRCODE
		(
		   repr_cd
         , repr_nm
         , dsc
         , use_yn

		) VALUES (
		   #{repr_cd}
         , #{repr_nm}
         , #{dsc}
         , #{use_yn}

		)

mapper.update:
 	/* code.updateRepr */

	UPDATE TB_REPRCODE
	SET	   repr_nm = #{repr_nm}
         , dsc     = #{dsc}
         , use_yn  = #{use_yn}
	WHERE  repr_cd = #{repr_cd}

mapper.select:
 	/* code.reprDetail */

	SELECT *
	FROM TB_REPRCODE
	WHERE  repr_cd = #{repr_cd}

 	/* code.codeNameList */

	SELECT *
	FROM TB_CODE
	WHERE  repr_cd = #{repr_cd}
    AND    nm      = #{nm}
