mapper.select:

/* menu.menuList*/

SELECT  M.*

FROM tb_menu M
WHERE 0 = 0
  /*AND M.USE_YN = 'Y'*/
ORDER BY M.MENU_DEPTH, M.SORT_SEQ


/* menu.menuCount*/

SELECT  count(*) 
FROM tb_menu M 
WHERE 0 = 0
  /*AND M.USE_YN = 'Y'*/


 	/* menu.menuDetail */

	SELECT *
	FROM tb_menu 
	WHERE  menu_id = #{menu_id}

mapper.insert:

	 	/* menu.insertMenu */

		INSERT INTO TB_MENU
			(
			      MENU_NM
				, MENU_DEPTH
				, MENU_URL

				 , UP_MENU_ID  
				 , SORT_SEQ  

				, ETC
				, URL_ROLE
				, USE_YN
				, REG_DT

				 , REG_USER_NO    	


			) VALUES (
			      #{menu_nm}
				, #{menu_depth}
                , #{menu_url}
				 , #{up_menu_id}  
				 , #{sort_seq} 

				, #{etc}
				, #{url_role}
				, #{use_yn}

				, DATE_FORMAT(now()	, '%Y%m%d%H%i%s') 

				 , #{reg_user_no}     	

			)


 	/* menu.updateMenu */

	UPDATE TB_MENU
	SET       MENU_NM       = #{menu_nm}
			, MENU_URL      = #{menu_url}


			 , SORT_SEQ = #{sort_seq} 

			, ETC          = #{etc}
			, URL_ROLE     = #{url_role}
			, USE_YN       = #{use_yn}
			, UPD_DT       = DATE_FORMAT(now()	, '%Y%m%d%H%i%s') 

			 , UPD_USER_NO =  #{reg_user_no}     	
	 WHERE menu_id = #{menu_id}
